math_visualizations.concise:
/* layout=dot */
digraph PointPredictionFormula {
  rankdir=LR;
  node [shape=plaintext, fontsize=14];
  edge [fontsize=12];

  Y_hat_star [label="Ŷ*"];
  equals [label="=", shape=none, fontsize=16];
  b0 [label="b₀"];
  plus [label="+", shape=none, fontsize=16];
  b1 [label="b₁"];
  X_star [label="X*"];

  Y_hat_star -> equals [style=invis];
  equals -> b0 [style=invis];
  b0 -> plus [style=invis];
  plus -> b1 [style=invis];
  b1 -> X_star [style=invis];
}

/* layout=dot */
digraph LogTransformation {
    rankdir=LR;
    node [shape=box];
    Y [label="Y"];
    logY [label="log(Y)"];
    X [label="X"];
    logX [label="log(X)"];

    Y -> logY [label="Apply Logarithm"];
    X -> logX [label="Apply Logarithm"];
}

/* layout=dot */
digraph InverseTransformation {
    rankdir=LR;
    node [shape=box];
    X [label="X"];
    inverseX [label="1/X"];

    X -> inverseX [label="Apply Inverse"];
}

/* layout=dot */
digraph QuadraticModel {
    rankdir=LR;
    node [shape=box];
    Y_hat [label="Ŷ"];
    b0 [label="b₀"];
    b1X [label="b₁X"];
    b2X2 [label="b₂X²"];
    plus1 [label="+", shape=none];
    plus2 [label="+", shape=none];

    Y_hat -> b0 [label="=", style=bold];
    Y_hat -> plus1 [style=invis];
    plus1 -> b1X;
    plus1 -> plus2 [style=invis];
    plus2 -> b2X2;
}

math_visualizations.example:
/* layout=neato */
digraph QuadraticExample {
    node [shape=circle, fixedsize=true, width=0.5];
    graph [splines=true];

    subgraph cluster_axes {
        style=invis;
        a [pos="0,0!", label="", width=0.01];
        b [pos="10,0!", label="X", width=0.01];
        c [pos="0,5!", label="Y", width=0.01];
        }

    node [shape=point, width=0.01];
    p1 [pos="1,1!"];
    p2 [pos="2,3!"];
    p3 [pos="3,4!"];
    p4 [pos="4,4.5!"];
    p5 [pos="5,4.7!"];
    p6 [pos="6,4.5!"];
    p7 [pos="7,4!"];
    p8 [pos="8,3!"];
    p9 [pos="9,1!"];

    a -> p1 -> p2 -> p3 -> p4 -> p5 -> p6 -> p7 -> p8 -> p9 -> b;
    a -> c;
}
