mermaid_diagrams.concise:
graph TD
    A[Growth Share Matrix] --> B{Portfolio Management Framework}
    B --> C{Prioritize Businesses}
    B --> D{Based on Profitability}
    C -- Factors --> E[Market Share]
    C -- Factors --> F[Market Growth]

math_visualizations.concise:
/* layout=neato */
digraph G {
    node [margin=0.3, fontsize=11, shape=box];
    edge [penwidth=1.5];

    MarketShare [label="Market Share
(Relative)", pos="0,0!"];
    GrowthRate [label="Market Growth
Rate", pos="0,10!"];

    subgraph cluster_matrix {
        label="Growth Share Matrix";
        style=filled;
        color=lightgrey;

        HighGrowth [label="High Growth", shape=none, pos="-2,7!"];
        LowGrowth [label="Low Growth", shape=none, pos="-2,3!"];
        HighShare [label="High Share", shape=none, pos="3,12!"];
        LowShare [label="Low Share", shape=none, pos="7,12!"];

        Star [label="Star
(High Share, High Growth)", pos="3,7!", fillcolor=yellow, style=filled];
        QuestionMark [label="Question Mark
(Low Share, High Growth)", pos="7,7!", fillcolor=lightblue, style=filled];
        CashCow [label="Cash Cow
(High Share, Low Growth)", pos="3,3!", fillcolor=lightgreen, style=filled];
        Pet [label="Pet (Dog)
(Low Share, Low Growth)", pos="7,3!", fillcolor=salmon, style=filled];
    }

    edge [style=invis];
    MarketShare -> HighShare;
    MarketShare -> LowShare;
    GrowthRate -> HighGrowth;
    GrowthRate -> LowGrowth;
}

