question_visual.code:
digraph G {
    rankdir=LR;
    node [shape=box, style=filled, fillcolor=lightgray, margin=0.3, fontsize=11];
    edge [dir=forward, arrowhead=open];

    TPS [label="Standard TPS
(Structured Decisions)", fillcolor=lightgreen];
    RoutineTasks [label="Routine Operations"];
    UnexpectedFailure [label="Unexpected Machine Failure
(Novel Problem)", fillcolor=salmon];
    SupervisorDecision [label="Supervisor's Decision
(Ad-hoc)", fillcolor=lightblue];

    TPS -> RoutineTasks [label="Automated Process"];
    RoutineTasks -> TPS [label="Input/Feedback"];

    UnexpectedFailure -> SupervisorDecision [label="Requires Immediate Action"];

    subgraph cluster_future_adaptation {
        label="Future System Adaptation";
        color=blue;
        style=filled;
        fillcolor="#F0F8FF";

        HybridSystem [label="Hybrid System
(TPS + Exception Handling)", fillcolor=lightgoldenrod];
        DSS_Module [label="DSS Module
(Scenario Analysis)", shape=oval];

        TPS -> HybridSystem [label="Integrates With"];
        UnexpectedFailure -> DSS_Module [label="Feeds Data To"];
        DSS_Module -> HybridSystem [label="Supports Supervisor"];
        HybridSystem -> SupervisorDecision [label="Empowers Decision"];
    }
}

digraph G {
    rankdir=LR;
    node [shape=box, style=filled, fillcolor=lightgray, margin=0.3, fontsize=11];
    edge [dir=forward, arrowhead=open];

    SchedulingDecision [label="Nursing Staff Scheduling
(Semistructured)", fillcolor=lightblue];
    PatientLoad [label="Patient Load"];
    NurseAvail [label="Nurse Availability"];
    LaborCosts [label="Labor Costs"];
    NewRegulation [label="New Federal Regulation
(Strict Ratio for Specialized Procs)", fillcolor=salmon];

    SchedulingDecision -> PatientLoad;
    SchedulingDecision -> NurseAvail;
    SchedulingDecision -> LaborCosts;

    NewRegulation -> SchedulingDecision [label="Introduces New Constraint"];

    subgraph cluster_impact {
        label="Impact on Decision Type";
        color=blue;
        style=filled;
        fillcolor="#F0F8FF";

        IncreasedStructured [label="Increased Structuredness
(for specialized procs)", fillcolor=lightgreen];
        ReducedFlexibility [label="Reduced Scheduling Flexibility"];

        NewRegulation -> IncreasedStructured;
        IncreasedStructured -> ReducedFlexibility;
    }
}

