question_visual.diagram:
flowchart TD;
    CustomerData[Historical Customer Data
(Features)] --> ChurnLabel[Churn Status (Label)];
    ChurnLabel --> Training[Train Churn Prediction Model];
    Training --> Model[Deployed Model];

flowchart TD;
    CustomerData[Historical Customer Data
(Features)] --> RepeatPurchaseFlag[Repeat Purchase Status (Label)];
    RepeatPurchaseFlag --> Training[Train Prediction Model];
    Training --> Model[Deployed Model];
    NewCustomer[New Customer Data] --> Model;
    Model --> Prediction[Prediction: Repeat Purchase Yes/No];

graph TD;
    A[Input Data (Features)] --> B{Supervised Learning Algorithm};
    B --> C[Labeled Output (Target)];
    C --> D[Model Training];

graph TD;
    subgraph Common Mistake
        UnlabeledData[Unlabeled Data or
        Poor Labels] --> ModelFails[Model Fails to Learn];
    end

flowchart TD;
    HouseFeatures[House Features
    (SqFt, Beds, Loc)] --> ActualPrice[Actual Selling Price
    (Label)];
    ActualPrice --> TrainModel[Train Price Prediction Model];
    TrainModel --> DeployedModel[Deployed Pricing Model];
