math_visualizations.concise:
/* layout=neato */
digraph G {
    node [shape=box, style=filled, color="#E0E0E0", margin=0.3, fontsize=11, fontcolor=black];
    edge [color=black, penwidth=1.5];

    P_Qty [label="Producer:\nHuge Quantities", pos="-2,2!"];
    P_Assort [label="Producer:\nNarrow Assortment", pos="-2,1!"];
    P_Geo [label="Producer:\nConcentrated Location", pos="-2,0!"];

    C_Qty [label="Consumer:\nSmall Quantities", pos="2,2!"];
    C_Assort [label="Consumer:\nBroad Assortment", pos="2,1!"];
    C_Geo [label="Consumer:\nDispersed Location", pos="2,0!"];

    P_Qty -> C_Qty [label="Quantity Gap", style=dashed, color=red];
    P_Assort -> C_Assort [label="Assortment Gap", style=dashed, color=red];
    P_Geo -> C_Geo [label="Place Gap", style=dashed, color=red];

    label="Producer vs. Consumer Differences";
    labelloc="t";
    labeljust="c";
    bgcolor="#FFFFFF";
}

