math_visualizations.common_mistakes:
/* layout=dot */
digraph G {
    node [shape=box, style=filled, margin=0.3, fontsize=11, fontcolor=black];
    edge [penwidth=1.5];

    subgraph cluster_mistake {
        label="Common Mistake";
        color=red;
        P_Focus [label="Producer Focus:\nHigh Volume, Low Cost"];
        C_Ignore [label="Ignore Consumer Needs"];
        Gaps_Unaddressed [label="Unaddressed Marketing Gaps"];
        Negative_Outcome [label="Unsold Inventory,\nCustomer Dissatisfaction"];

        P_Focus -> C_Ignore;
        C_Ignore -> Gaps_Unaddressed;
        Gaps_Unaddressed -> Negative_Outcome;
    }

    subgraph cluster_correct {
        label="Correct Approach";
        color=green;
        P_C_Balance [label="Balance Production & Consumer Needs"];
        Channel_Bridge [label="Channels Bridge Gaps Strategically"];
        Positive_Outcome [label="Sales, Customer Loyalty"];

        P_C_Balance -> Channel_Bridge;
        Channel_Bridge -> Positive_Outcome;
    }

    P_Focus -> P_C_Balance [style=dashed, color=gray, label="Shift Focus"];

    label="Impact of Addressing Marketing Gaps";
    labelloc="t";
    labeljust="c";
    bgcolor="#FFFFFF";
}

