    rankdir=LR;
    node [margin=0.3, fontsize=11, shape=box];
    edge [dir=both, arrowhead=normal, arrowtail=normal];

    Direct_Control [label="Direct: High Control
High Per-Unit Margin"];
    Direct_Cost [label="Direct: High Logistics Cost
High Marketing Investment"];
    Indirect_Reach [label="Indirect: Broad Reach
Leverage Existing Networks"];
    Indirect_Margin [label="Indirect: Lower Per-Unit Margin
Less Control"];

    Mistake [label="Mistake: Assume Direct is always best"];
    Reality [label="Reality: Evaluate Trade-offs"];

    Direct_Control -> Direct_Cost [label="Trade-off"];
    Indirect_Reach -> Indirect_Margin [label="Trade-off"];
    Mistake -> {Direct_Cost; Indirect_Margin} [label="Leads to ignoring critical factors"];
    Reality -> {Direct_Control; Direct_Cost; Indirect_Reach; Indirect_Margin} [label="Considers all factors"];
}

