math_visualizations.concise:
/* layout=dot */ digraph G {
    rankdir=TB;
    node [margin=0.3, fontsize=11, shape=box];

    Producer [label="P_P"];
    Wholesaler [label="P_W"];
    Retailer [label="P_R"];
    System [label="P_System"];

    Producer -> Wholesaler [label="Maximize P_P"];
    Wholesaler -> Retailer [label="Maximize P_W"];
    Retailer -> System [label="Maximize P_R"];

    {rank=same; Producer; Wholesaler; Retailer;}
    System [shape=oval, style=dashed, label="P_System = P_P + P_W + P_R
(often < Max(P_P+P_W+P_R))"];

    Producer -> System [style=dashed, color=red];
    Wholesaler -> System [style=dashed, color=red];
    Retailer -> System [style=dashed, color=red];

    label = "Independent Profit Maximization";
    labelloc = "t";
    fontsize = 12;
}

