question_visual.dot_code:
/* layout=neato */
graph G {
    node [shape=box, style=filled, fillcolor="#E0F2F7", margin=0.3, fontsize=11];
    edge [color="#00796B", penwidth=1.5];

    X_axis [label="Time", pos="0,0!"];
    Y_axis [label="Performance", pos="0,10!"];

    L_low [label="Low quality use", pos="1,2!"];
    L_medium [label="Medium quality use", pos="1,4!"];
    L_high [label="High quality use", pos="1,6!"];
    L_demanding [label="Most demanding use", pos="1,8!"];

    DT_start [label="Disruptive Technology", pos="1,1.5!"];
    DT_mid [label="", pos="5,5!"];
    DT_end [label="", pos="9,9!"];

    DT_start -> DT_mid [label="Rapid Improvement", style=dashed, penwidth=2, color="#D81B60"];
    DT_mid -> DT_end [label="Accelerated Growth", style=dashed, penwidth=2, color="#D81B60"];

    // Invisible edges to position labels relative to axes
    Y_axis -> X_axis [style=invis, len=1];

    // Draw the S-shaped curve for Disruptive Technology visually
    subgraph cluster_disruptive_curve {
        color=none;
        DT_p0 [pos="0.5,1.5!"];
        DT_p1 [pos="2,2!"];
        DT_p2 [pos="4,4!"];
        DT_p3 [pos="6,6!"];
        DT_p4 [pos="8,8!"];
        DT_p5 [pos="9.5,9.5!"];
        DT_p0 -> DT_p1 [dir=none, penwidth=2, color="#D81B60"];
        DT_p1 -> DT_p2 [dir=none, penwidth=2, color="#D81B60"];
        DT_p2 -> DT_p3 [dir=none, penwidth=2, color="#D81B60"];
        DT_p3 -> DT_p4 [dir=none, penwidth=2, color="#D81B60"];
        DT_p4 -> DT_p5 [dir=none, penwidth=2, color="#D81B60"];
    }

    DT_start -> L_low [style=invis, len=0.1];
    DT_mid -> L_medium [style=invis, len=0.1];
    DT_end -> L_high [style=invis, len=0.1];
}

/* layout=dot */
digraph G {
    node [shape=box, margin=0.3, fontsize=11];
    edge [penwidth=1.5];

    subgraph cluster_correct {
        label="Correct (OLTP vs OLAP)";
        style=filled; fillcolor="#D4EDDA";
        C_OLTP_DB [label="OLTP DB\n(Fast Writes)"];
        C_ETL_Proc [label="ETL Process"];
        C_OLAP_DW [label="OLAP DW\n(Fast Reads)"];
        C_OLTP_DB -> C_ETL_Proc -> C_OLAP_DW;
    }

    subgraph cluster_mistake {
        label="Mistake (Using OLTP for OLAP)";
        style=filled; fillcolor="#F8D7DA";
        M_OLTP_DB [label="OLTP DB\n(Writes + Reads)"];
        M_ComplexQuery [label="Complex Analytical\nQuery"];
        M_Slow [label="Slow Performance"];
        M_OLTP_DB -> M_ComplexQuery -> M_Slow;
    }
}

