question_visual.code:
/* layout=dot */
digraph G {
    rankdir=LR;
    node [shape=box, margin=0.3, fontsize=11];
    subgraph cluster_0 {
        label="Effective (Hybrid)";
        Known_Frauds [label="Known Frauds"];
        New_Frauds [label="New Frauds"];
        Supervised_F [label="Supervised Model"];
        Unsupervised_F [label="Unsupervised Model"];
        Catch_All [label="Catch All Frauds"];
        Known_Frauds -> Supervised_F;
        New_Frauds -> Unsupervised_F;
        Supervised_F -> Catch_All;
        Unsupervised_F -> Catch_All;
    }
    subgraph cluster_1 {
        label="Ineffective (Supervised Only)";
        Known_Frauds_S [label="Known Frauds"];
        New_Frauds_S [label="New Frauds"];
        Supervised_Only [label="Supervised Model Only"];
        Missed_New [label="Missed New Frauds"];
        Known_Frauds_S -> Supervised_Only;
        New_Frauds_S -> Missed_New [color=red, style=dashed];
    }
}

