  Yi [label="Yᵢ", fontsize=14];
  eq1 [label="=", shape=plaintext, fontsize=14];
  beta0 [label="β₀", fontsize=12];
  plus1 [label="+", shape=plaintext, fontsize=14];
  term1 [label="β₁X₁ᵢ", fontsize=12];
  plus2 [label="+", shape=plaintext, fontsize=14];
  term2 [label="β₂X₂ᵢ", fontsize=12];
  plus3 [label="+", shape=plaintext, fontsize=14];
  dots [label="⋯", shape=plaintext, fontsize=14];
  plus4 [label="+", shape=plaintext, fontsize=14];
  termk [label="βₖXₖᵢ", fontsize=12];
  plus5 [label="+", shape=plaintext, fontsize=14];
  epsilon [label="εᵢ", fontsize=12];

  Yi -> eq1 -> beta0 -> plus1 -> term1 -> plus2 -> term2 -> plus3 -> dots -> plus4 -> termk -> plus5 -> epsilon;
}

