  CI_formula [label="CI: Ŷ ±\nt* × SE(Ŷ)", fillcolor="lightyellow"];
  PI_formula [label="PI: Ŷ ±\nt* × √[SE²(Ŷ)\n+ σ²]", fillcolor="lightyellow"];

  CI_purpose [label="Estimates\nmean E(Y|X)", fillcolor="white"];
  PI_purpose [label="Predicts\nindividual Y", fillcolor="white"];

  width [label="PI is\nalways\nwider", shape=note, fillcolor="lightpink"];

  CI -> CI_formula -> CI_purpose;
  PI -> PI_formula -> PI_purpose;
  PI -> width [style=dashed];
  CI -> width [style=dashed];
}

