graph TD;
    A[Unusual Observations] --> B[High Leverage (Extreme X-value)];
    A --> C[Vertical Outlier (Large Residual)];
    B & C --> D[Influential Observation (Changes Regression Line)];
    P1[P1] -- High Leverage --> B;
    P1 -- Small Residual --> C_No[No Vertical Outlier];
    P2[P2] -- Large Residual --> C;
    P2 -- Pulls Line --> D;
    P3[P3] -- Large Residual --> C;
    P3 -- Does NOT Pull Line --> D_No[Not Influential];

\text{Var}(e_i) = \sigma^2 (1 - h_{ii})
\text{where } h_{ii} \text{ is the leverage of observation } i.
\text{Higher leverage } (h_{ii}) \text{ means lower variance of the residual.}

